    public void getHDetails()
    {
        DBConnect dbc = new DBConnect();
        String sql="Select phonenumber from houseownerdetails where email = '"+hemail+"';";
        try{
            rs = dbc.getDetails(sql);
            while(rs.next())
            {
                cPhone.setText(rs.getString("phonenumber"));
            }
        }catch(Exception e){
            System.out.println("Error : " + e);
            e.printStackTrace();
        }
    }

