    private void choosePhotoButttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_choosePhotoButttonActionPerformed
        // TODO add your handling code here:
        
        //Add Image
        
        JFileChooser jc = new JFileChooser();
        jc.showOpenDialog(null);
        //jc.setMultiSelectionEnabled(true);
        /*jc.setFileFilter(new FileTypeFilter(".jpg", "JPG"));
        jc.setFileFilter(new FileTypeFilter(".jpeg", "JPEG"));
        jc.setFileFilter(new FileTypeFilter(".png", "PNG"));
        jc.setFileFilter(new FileTypeFilter(".gif", "GIF"));*/
        File f = jc.getSelectedFile();
        
        abspath = f.getAbsolutePath();
        filename = f.getName();
        choosePhoto.setForeground(Color.BLACK);
        choosePhoto.setText(filename);
        try{
            File image = new File(abspath);
            FileInputStream fis = new FileInputStream(image);
            ByteArrayOutputStream bos = new ByteArrayOutputStream();
            byte[] buf = new byte[1024];
            for(int readNum; (readNum = fis.read(buf)) != -1; )
            {
                bos.write(buf, 0, readNum);
            }
            photo = bos.toByteArray();
            
        }catch(IOException e){
            //System.out.println("Error : " + e);
            JOptionPane.showMessageDialog(null, "Photo size crossed");
        }
        //add Image end
        
    }//GEN-LAST:event_choosePhotoButttonActionPerformed

