    private void submitAdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitAdActionPerformed
        // TODO add your handling code here:
        
        String title = AdTitle.getText();
        String month = monthBox.getSelectedItem().toString();
        String year = yearBox.getSelectedItem().toString();
        String date = month + ", " + year;
        
        String specialFacility = null;
        
        if(bathBox.isSelected() == true)
        {
            specialFacility = "Attached Bathroom";
        }
        else if(kitchen.isSelected() == true)
        {
            specialFacility = "Kitchen";
        }
        else if(corri.isSelected() == true)
        {
            specialFacility = "Corridor";
        }
        else if(bathBox.isSelected() == true && kitchen.isSelected() == true)
        {
            specialFacility = "Attached Bathroom, Kitchen";
        }
        else if(kitchen.isSelected() == true && corri.isSelected() == true)
        {
            specialFacility = "Kitchen, Corridor";
        }
        else if(bathBox.isSelected() == true && corri.isSelected() == true)
        {
            specialFacility = "Attached Bathroom, Corridor";
        }
        else if(bathBox.isSelected() == true && corri.isSelected() == true && kitchen.isSelected() == true)
        {
            specialFacility = "Attached Bathroom, Kitchen, Corridor";
        }
        String rent = Rent.getText();
        String rtype = null;
        if(fixedRent.isSelected() == true)
        {
            rtype ="(Fixed)";
        }
        else if(negRent.isSelected() == true)
        {
            rtype ="(Negotiable)";
        }
        
        String address = hAddress.getText();
        String city = cityBox.getSelectedItem().toString();
        String area = areaBox.getSelectedItem().toString();
        String details = hDetails.getText();
        String name = CName.getText();
        String phone = cPhone.getText();
        String email = CEmail.getText();
        
        String userid = null;
        DBConnect dbc = new DBConnect();
        try{
            String sql = "Select userid from houseownerdetails where email = '"+hemail+"'";
            rs = dbc.getDetails(sql);
            while(rs.next())
            {
                userid = rs.getString("userid");
            }
        }catch(SQLException e){
            System.out.println("Error : " + e);
        }
        
        //database connection
        try {
            Class.forName("com.mysql.jdbc.Driver");
            // step 1 > get a connection to database
            
            con = DriverManager.getConnection("jdbc:mysql://localhost:3306/accommodationsystem", "root", "");
            //step 2> create a statement
            st = con.createStatement();
            
        } catch (Exception e) {
            System.out.println("Error : " + e);
            e.printStackTrace();
        }
        
        try{
            String sqlQuery = "insert into subletdetails(title, date, specialfacility, rent, address, city, area, details, photo, name, email, phone, renttype, userid) "
                    + "values(?,?,?,?,?,?,?,?,?,?,?,?,?,?);";
            
            pst = con.prepareCall(sqlQuery);
            pst.setString(1, title);
            pst.setString(2, date);
            pst.setString(3, specialFacility);
            pst.setString(4, rent);
            pst.setString(5, address);
            pst.setString(6, city);
            pst.setString(7, area);
            pst.setString(8, details);
            pst.setBytes(9, photo);
            pst.setString(10, name);
            pst.setString(11, email);
            pst.setString(12, phone);
            pst.setString(13, rtype);
            pst.setString(14, userid);
            
            pst.executeUpdate();
            JOptionPane.showMessageDialog(null, "Ads Posted! Wait For Verification");
            this.setVisible(false);
            OwnerMainPage omp = new OwnerMainPage(hemail, hname);
            omp.setVisible(true);
            
        }catch(HeadlessException | SQLException e)
        {
            JOptionPane.showMessageDialog(null, "Enter All Data Correctly");
            //System.out.println("Error : " + e);
        }
        
    }//GEN-LAST:event_submitAdActionPerformed

